function main() {
  var n = parseInt(readLine());
  var stairs = "";

  for (var i = 1; i <= n; i++) {

    var spaces = n - i;
    var hashes = i;

    while (spaces > 0) {
      stairs += " ";
      spaces--;
    }

    while (hashes > 0) {
      stairs += "#";
      hashes--;
    }

    stairs += "\n";
  }

  console.log(stairs);

}
