function main() {
  "use strict";

  // Convert matrix to 2d array
  var n = parseInt(readLine());
  var a = [];
  for(var a_i = 0; a_i < n; a_i++){
     a[a_i] = readLine().split(' ');
     a[a_i] = a[a_i].map(Number);
  }

  // So we want to add a[0][0] with a[1][1] with a[2][2] with a[3][3]
  // Which is equivalent to:
  var diag = 0;
  var diag2 = 0;

  for (let i = 0; i < a.length; i++) {
    diag += a[i][i];
  }

  // Then we want to add a[0][3] + a[1][2] + a[2][1] + a[3][0]
  // So we set up a counter called firstDim (first dimension on array)
  var firstDim = 0;
  for (let i = n - 1; i >= 0; i--) {
    diag2 += a[firstDim][i];
    firstDim++;
  }

  // Then we subtract the first and second diagonal and absolute it
  console.log(Math.abs(diag - diag2));
}
