function total(input) {
  //Enter your code here
  "use strict";

  var arr = input.split(/\n| /),
      m = Number(arr[0]),
      t = Number(arr[1]),
      x = Number(arr[2]);

  var tip = (t * m / 100).toFixed(2);
  var tax = (x * m / 100).toFixed(2);
  var price = (m + Number(tip) + Number(tax));

  console.log("The final price of the meal is $" + Math.round(price) + ".");
}
