var $buttonInner = $("button[name*=inner]");

$buttonInner.click(function(event) {
  event.preventDefault();
  var $divWidth = $("div").innerWidth();
  var $divHeight = $("div").innerHeight();
  $("div").text("This box's width is " + $divWidth + " and it's height is " + $divHeight);
});
