@media (min-width: 768px) {
  .flex-container-row {
    display: -webkit-flex;
   display: flex;
   -webkit-flex-direction: row;
   flex-direction: row;
  }
  .vert-mid-parent {
    position: relative;
    min-height: 100%;
  }
  .vert-mid-child {
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
    display: block;
  }
  .arrow-right:after {
    content: "\25ba";
    color: #f79423;
    padding-left: 5px;
  }
}

p {
  width: 100%;
  text-align: right;
}
